<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Carte Conceptuelle — Anomalies Cliniques & Pharmacologiques NAC (2024-2025)</title>
    <script type="text/javascript" src="https://unpkg.com/vis-network/standalone/umd/vis-network.min.js"></script>
    <style>
        body { margin: 0; padding: 0; background-color: #1a1a1a; font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; color: #e0e0e0; overflow: hidden; }
        #map { width: 100vw; height: 100vh; border: none; }
        .legend { position: absolute; top: 10px; left: 10px; background: rgba(0, 0, 0, 0.8); padding: 15px; border-radius: 8px; border: 1px solid #444; pointer-events: none; }
        .legend h3 { margin: 0 0 10px 0; font-size: 14px; text-transform: uppercase; color: #4285F4; }
        .legend-item { display: flex; align-items: center; margin-bottom: 5px; font-size: 12px; }
        .dot { width: 10px; height: 10px; border-radius: 50%; margin-right: 8px; }
        #loading { position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%); color: #4285F4; font-size: 24px; font-weight: bold; }
    </style>
</head>
<body>
    <div id="loading">INITIALISATION DU RÉSEAU NEURAL...</div>
    <div class="legend">
        <h3>Légende des Flux</h3>
        <div class="legend-item"><div class="dot" style="background:#4285F4"></div>Concept Central / Protocole</div>
        <div class="legend-item"><div class="dot" style="background:#EA4335"></div>Anomalie / Échec / Blocage</div>
        <div class="legend-item"><div class="dot" style="background:#FBBC05"></div>Paradoxe Clinique</div>
        <div class="legend-item"><div class="dot" style="background:#34A853"></div>Succès / Synergie / Solution</div>
        <div class="legend-item"><div class="dot" style="background:#9334E6"></div>Dose Massive / Futur</div>
    </div>
    <div id="map"></div>

    <script type="text/javascript">
        // Configuration des Données (Noeuds et Liens)
        const nodes = new vis.DataSet([
            // NOYAU CENTRAL
            { id: 1, label: "NAC\n(N-Acétylcyctéine)", title: "<b>Molécule:</b> Précurseur GSH & Modulateur Glutamate", group: "core", value: 30 },
            { id: 2, label: "Protocole\nTriple Aveugle", title: "<b>Méthodologie:</b> Gold Standard (2024-2025) pour éliminer le biais", group: "core", value: 25 },

            // BRANCHE 1: ANOMALIE NEUROPATHIE (CIPN)
            { id: 3, label: "Échec CIPN\n(Neuropathie)", title: "<b>Résultat:</b> Aucune différence vs Placebo", group: "fail", value: 20 },
            { id: 4, label: "Taxanes\n(Chimio)", title: "<b>Inducteur:</b> Stress oxydatif massif", group: "fail", value: 15 },
            { id: 5, label: "Dose Orale\n1200 mg/j", title: "<b>Protocole Testé:</b> Dose standard historique", group: "fail", value: 15 },
            { id: 6, label: "Premier Passage\nHépatique", title: "<b>Barrière:</b> Bloque la biodisponibilité systémique", group: "fail", value: 20 },

            // BRANCHE 2: PARADOXE PSYCHOSE (ENACT)
            { id: 7, label: "Essai ENACT\n(Psychose)", title: "<b>Étude:</b> Premier Épisode Psychotique", group: "paradox", value: 20 },
            { id: 8, label: "Symptômes\nPositifs", title: "<b>Anomalie:</b> Pas d'effet sur les hallucinations (PANSS)", group: "fail", value: 15 },
            { id: 9, label: "Fonction\nCognitive", title: "<b>Succès:</b> Amélioration vitesse traitement & fluence", group: "success", value: 15 },
            { id: 10, label: "Target Engagement\n(GSH Cérébral)", title: "<b>Preuve Bio:</b> Augmentation GSH confirmée par MRS", group: "success", value: 15 },

            // BRANCHE 3: ANOMALIE CARDIAQUE
            { id: 11, label: "Échec PCI\n(Infarctus)", title: "<b>Anomalie:</b> Administration Intracoronaire inefficace", group: "fail", value: 15 },
            { id: 12, label: "Mécanisme\nAntiplaquettaire", title: "<b>Constat:</b> In vitro positif mais échec clinique aigu", group: "fail", value: 10 },

            // BRANCHE 4: RÉSOLUTION PAR DOSE MASSIVE
            { id: 13, label: "Dose Massive\n(3600-5400 mg)", title: "<b>Nouvelle Norme:</b> Nécessaire pour Neuro/Ophtalmo", group: "future", value: 25 },
            { id: 14, label: "Essai\nNAC Attack", title: "<b>Ophtalmologie:</b> Rétinite Pigmentaire (Phase III)", group: "future", value: 15 },
            { id: 15, label: "Automutilation\n(NSSI)", title: "<b>Psychiatrie:</b> Jusqu'à 5.4g pour contrôle impulsion", group: "future", value: 15 },

            // BRANCHE 5: SYNERGIE
            { id: 16, label: "Vitamine D", title: "<b>Co-facteur:</b> Agent de longévité", group: "success", value: 15 },
            { id: 17, label: "Effet\nSénolytique", title: "<b>Résultat:</b> Synergie anti-âge amplifiée", group: "success", value: 20 }
        ]);

        const edges = new vis.DataSet([
            // Structure Centrale
            { from: 1, to: 2, label: "soumis à", arrows: "to" },

            // Anomalie CIPN (Échec Dose)
            { from: 2, to: 3, label: "révèle", arrows: "to", color: { color: "#EA4335" } },
            { from: 3, to: 4, label: "lié aux", arrows: "to" },
            { from: 3, to: 5, label: "utilisait", arrows: "to" },
            { from: 5, to: 6, label: "neutralisée par", arrows: "to", dashes: true },
            { from: 6, to: 3, label: "cause l'échec", arrows: "to" },

            // Paradoxe Psychose
            { from: 2, to: 7, label: "analyse", arrows: "to" },
            { from: 7, to: 8, label: "inefficace sur", arrows: "to", color: { color: "#EA4335" } },
            { from: 7, to: 9, label: "améliore", arrows: "to", color: { color: "#34A853" } },
            { from: 7, to: 10, label: "valide", arrows: "to" },
            { from: 10, to: 9, label: "soutient", arrows: "to" },

            // Anomalie Cardiaque
            { from: 2, to: 11, label: "infirme hypothèse", arrows: "to" },
            { from: 11, to: 12, label: "contredit", arrows: "to" },

            // Résolution (Dose Massive)
            { from: 3, to: 13, label: "implique besoin", arrows: "to", dashes: true },
            { from: 1, to: 13, label: "tendance 2025", arrows: "to" },
            { from: 13, to: 14, label: "appliqué dans", arrows: "to" },
            { from: 13, to: 15, label: "testé pour", arrows: "to" },

            // Synergie
            { from: 1, to: 16, label: "combiné avec", arrows: "to" },
            { from: 16, to: 17, label: "génère", arrows: "to" },
            { from: 1, to: 17, label: "amplifie", arrows: "to", color: { color: "#34A853" } }
        ]);

        // Configuration Visuelle (Style Hacker/Executive)
        const options = {
            nodes: {
                shape: "dot",
                font: { size: 14, color: "#e0e0e0", face: "Segoe UI" },
                borderWidth: 2,
                shadow: true
            },
            edges: {
                width: 2,
                font: { size: 11, color: "#888", align: "middle" },
                smooth: { type: "continuous" },
                arrows: { to: { scaleFactor: 0.8 } }
            },
            groups: {
                core: { color: { background: "#4285F4", border: "#8AB4F8" }, shape: "diamond", size: 30 },
                fail: { color: { background: "#EA4335", border: "#F28B82" }, font: { color: "#F28B82" } },
                paradox: { color: { background: "#FBBC05", border: "#FDD663" }, font: { color: "#FDD663" } },
                success: { color: { background: "#34A853", border: "#81C995" }, font: { color: "#81C995" } },
                future: { color: { background: "#9334E6", border: "#C58AF9" }, font: { color: "#C58AF9" } }
            },
            physics: {
                solver: "forceAtlas2Based",
                forceAtlas2Based: {
                    gravitationalConstant: -80,
                    centralGravity: 0.005,
                    springLength: 150,
                    springConstant: 0.08,
                    damping: 0.4
                },
                stabilization: {
                    enabled: true,
                    iterations: 1000,
                    updateInterval: 25
                }
            },
            interaction: {
                hover: true,
                tooltipDelay: 200,
                zoomView: true
            }
        };

        // Initialisation
        const container = document.getElementById('map');
        const data = { nodes: nodes, edges: edges };
        const network = new vis.Network(container, data, options);

        // Events
        network.on("stabilizationIterationsDone", function () {
            document.getElementById('loading').style.display = 'none';
        });

        network.on("dragEnd", function (params) {
            if (params.nodes.length > 0) {
                nodes.update({ id: params.nodes[0], fixed: true });
            }
        });

        network.on("doubleClick", function (params) {
            if (params.nodes.length > 0) {
                nodes.update({ id: params.nodes[0], fixed: false });
            }
        });
    </script>
</body>
</html>
